import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

interface DrawingCanvasProps {
  mode: 'train' | 'test';
  onPrediction: (digit: number, confidence: number) => void;
  requestedDigit: number | null;
}

const DrawingCanvas: React.FC<DrawingCanvasProps> = ({
  mode,
  onPrediction,
  requestedDigit,
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [context, setContext] = useState<CanvasRenderingContext2D | null>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      if (ctx) {
        ctx.strokeStyle = 'white';
        ctx.lineWidth = 10;
        ctx.lineCap = 'round';
        ctx.lineJoin = 'round';
        setContext(ctx);
      }
    }
  }, []);

  const startDrawing = (e: React.MouseEvent<HTMLCanvasElement> | React.TouchEvent<HTMLCanvasElement>) => {
    setIsDrawing(true);
    const { offsetX, offsetY } = getCoordinates(e);
    context?.beginPath();
    context?.moveTo(offsetX, offsetY);
  };

  const draw = (e: React.MouseEvent<HTMLCanvasElement> | React.TouchEvent<HTMLCanvasElement>) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = getCoordinates(e);
    context?.lineTo(offsetX, offsetY);
    context?.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const getCoordinates = (e: React.MouseEvent<HTMLCanvasElement> | React.TouchEvent<HTMLCanvasElement>) => {
    if ('touches' in e) {
      const rect = canvasRef.current?.getBoundingClientRect();
      return {
        offsetX: e.touches[0].clientX - (rect?.left || 0),
        offsetY: e.touches[0].clientY - (rect?.top || 0),
      };
    }
    return {
      offsetX: e.nativeEvent.offsetX,
      offsetY: e.nativeEvent.offsetY,
    };
  };

  const clearCanvas = () => {
    if (context && canvasRef.current) {
      context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    }
  };

  const handleSubmit = async () => {
    if (!canvasRef.current) return;

    const imageData = canvasRef.current.toDataURL('image/png');

    try {
      const response = await axios.post(`http://localhost:8000/${mode}`, {
        image: imageData,
        digit: requestedDigit,
      });

      if (mode === 'test') {
        onPrediction(response.data.prediction, response.data.confidence);
      } else {
        clearCanvas();
        // Trigger new random digit request
      }
    } catch (error) {
      console.error('Error submitting drawing:', error);
    }
  };

  return (
    <div className="flex flex-col items-center gap-4">
      <canvas
        ref={canvasRef}
        width={280}
        height={280}
        className="border-2 border-gray-300 dark:border-gray-600 bg-black rounded-lg touch-none"
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
        onTouchStart={startDrawing}
        onTouchMove={draw}
        onTouchEnd={stopDrawing}
      />
      <div className="flex gap-4">
        <button
          onClick={clearCanvas}
          className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Clear
        </button>
        <button
          onClick={handleSubmit}
          className="px-4 py-2 bg-blue-600 text-white rounded"
        >
          {mode === 'train' ? 'Submit Training' : 'Test Prediction'}
        </button>
      </div>
    </div>
  );
};

export default DrawingCanvas;
